import React from "react";
import { Card, Col, Container, Row } from "react-bootstrap";
import ProjectCard from "../assets/images/project-card.png";
import Artboard from "../assets/images/artboard.svg";

export default function Clients() {
  return (
    <section className="py-5 position-relative">
      <small
        className="artboard-text position-absolute text-uppercase top-50 fw-bold d-none d-lg-block"
        style={{
          left: "0",
          color: "#ff6442",
          letterSpacing: "5px",
          transform: "rotate(-90deg)",
        }}
      >
        <img src={Artboard} alt="" />
        our clients
      </small>
      <Container>
        <Row className="mb-5">
          <Col lg={12} className="text-center">
            <p style={{ color: "#392390" }} className="main-title">
              Who we've worked with:
            </p>
          </Col>
        </Row>
        <Row className="text-center">
          <Col lg={9} className="d-flex flex-wrap mx-auto">
            <Row>
              {clientCardContents.map((content, i) => (
                <Col sm={3} xs={6} key={i}>
                  <Card
                    style={{ width: "10rem" }}
                    className="me-4 mb-4 border-0"
                  >
                    <Card.Img
                      variant="top"
                      className="img-fluid"
                      src={content.image}
                    />
                  </Card>
                </Col>
              ))}
            </Row>
          </Col>
        </Row>
      </Container>
    </section>
  );
}

const clientCardContents = [
  {
    image: ProjectCard,
  },
  {
    image: ProjectCard,
  },
  {
    image: ProjectCard,
  },
  {
    image: ProjectCard,
  },
  {
    image: ProjectCard,
  },
  {
    image: ProjectCard,
  },
  {
    image: ProjectCard,
  },
  {
    image: ProjectCard,
  },
  {
    image: ProjectCard,
  },
  {
    image: ProjectCard,
  },
  {
    image: ProjectCard,
  },
];
